
public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, k;
		i = 10;
		k = i < 0 ? -i : i;
		System.out.println("Absolute value of ");
		System.out.println(i + " is " + k);
		i = -10;
		k = i < 0 ? -i : i;
		System.out.println("Absolute value of k ");
		System.out.println(i + " is " + k);
		

	}

}
